(659, '//RUTA EN EL FILE EXPLORER DONDE SE GUARDA LA BD\r\n\r\n///data/data/NombreDelPaquete/databases/nombre_base_datos\r\n///data/data/bd/databases/UsuariosDB\r\n\r\npackage com.example.holamundo;\r\n\r\n\r\n\r\nimport android.app.Activity;\r\nimport android.database.sqlite.SQLiteDatabase;\r\nimport android.os.Bundle;\r\nimport android.util.Log;\r\nimport android.view.View;\r\nimport android.widget.EditText;\r\nimport android.widget.RadioButton;\r\nimport android.widget.Toast;\r\nimport bd.UsuariosSQLite;\r\n\r\npublic class Registro extends Activity {\r\n\r\n	private RadioButton r1,r2;\r\n	String sexo="";\r\n	SQLiteDatabase db;\r\n	EditText cedula, nombre, usuario, clave, correo, telefono, edad;\r\n	\r\n	\r\n	@Override\r\n	protected void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_registro);\r\n		inicializarVariables();\r\n		abrirBD();\r\n		\r\n	}\r\n	\r\n	private void abrirBD() {\r\n		// TODO Auto-generated method stub\r\n		   //Abrimos la base de datos\r\n        UsuariosSQLite usdb = new UsuariosSQLite(this, "UsuariosDB", null, 1);\r\n \r\n        //En modo escritura\r\n         db = usdb.getWritableDatabase();\r\n \r\n        //Si entra es porque se abrio la BD\r\n        if(db != null){\r\n        	Toast.makeText(this, "Conexion establecida", Toast.LENGTH_LONG).show();\r\n         cerrarBD();\r\n        }else{\r\n        	Toast.makeText(this, "Conexion NO establecida", Toast.LENGTH_LONG).show();\r\n\r\n        }\r\n	}\r\n\r\n	private void cerrarBD() {\r\n		// TODO Auto-generated method stub\r\n\r\n        //Cerramos la base de datos\r\n        db.close();\r\n        Toast.makeText(this, "Conexion cerrada", Toast.LENGTH_LONG).show();\r\n\r\n	}\r\n\r\n	private void inicializarVariables() {\r\n		// TODO Auto-generated method stub\r\n		 r1=(RadioButton)findViewById(R.id.rdbm);\r\n	     r2=(RadioButton)findViewById(R.id.rdbf);   \r\n	     cedula = (EditText)findViewById(R.id.txt_cedula);\r\n